import { Directive, HostListener, ElementRef } from '@angular/core';

@Directive({
  selector: '[appNumericInput]'
})
export class NumericInputDirective {

  constructor(private el: ElementRef) { }

  @HostListener('input', ['$event']) onInput(event: Event): void {
    const input = event.target as HTMLInputElement;
    const value = input.value.trim(); // Trim any leading/trailing whitespace

    // Check if the input is valid numeric characters
    if (!/^\d*$/.test(value)) {
      // If the input is not valid, sanitize the value
      input.value = value.replace(/[^\d]/g, '').slice(0, 10); // Allow only digits and limit to 10 characters
    }
  }

  @HostListener('keydown', ['$event']) onKeyDown(event: KeyboardEvent): void {
    // Allow Backspace, Delete, Arrow keys, Tab, and Enter
    if (event.key === 'Backspace' || event.key === 'Delete' ||
      event.key === 'ArrowLeft' || event.key === 'ArrowRight' ||
      event.key === 'Tab' || event.key === 'Enter') {
      return;
    }

    // Prevent '-' key from deleting the entire value
    if (event.key === '-' && !(this.el.nativeElement as HTMLInputElement).value.includes('-')) {
      event.preventDefault();
    }
  }

}
