import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-welcome-page',
  templateUrl: './welcome-page.component.html',
  styleUrls: ['./welcome-page.component.scss']
})
export class WelcomePageComponent implements OnInit {

  showPass: boolean = false
  currentPage: any = ''
  constructor(private activatedRoute: ActivatedRoute, private router: Router) {

  }
  ngOnInit(): void {
    this.activatedRoute.queryParamMap.subscribe((params: any) => {
      if (params?.params?.pageType) {
        this.currentPage = params?.params?.pageType
      }
    })
  }

  started(pageType: any) {
    if (pageType == 'Login') {
      this.currentPage = pageType;
    }
    if (pageType == 'Signup') {
      this.currentPage = pageType;
    }
    this.router.navigate(['/'], { queryParams: { pageType: this.currentPage } })
  }

  toggleInputClass(event: Event): void {
    const inputElement = event.target as HTMLInputElement;
    if (inputElement.value) {
      inputElement.classList.add('not-empty');
    } else {
      inputElement.classList.remove('not-empty');
    }
  }
}
